import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { api } from "../api";

export default function Editor() {
  const { id } = useParams();
  const [project, setProject] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const [refineText, setRefineText] = useState({});
  const [commentText, setCommentText] = useState({});
  const [exportLoading, setExportLoading] = useState(false);

  const loadProject = async () => {
    setLoading(true);
    try {
      const res = await api.get(`/projects/${id}`);
      setProject(res.data);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.detail || "Failed to load project");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadProject();
    // eslint-disable-next-line
  }, [id]);

  const refineSection = async (sectionId) => {
    const instruction = refineText[sectionId];
    if (!instruction) return;
    try {
      await api.post(`/projects/${id}/sections/${sectionId}/refine`, {
        instruction,
      });
      await loadProject();
    } catch (err) {
      console.error(err);
      alert("Refinement failed");
    }
  };

  const sendFeedback = async (sectionId, action) => {
    try {
      await api.post(`/projects/${id}/sections/${sectionId}/feedback`, {
        action,
        comment: commentText[sectionId] || null,
      });
      setCommentText((prev) => ({ ...prev, [sectionId]: "" }));
      await loadProject();
    } catch (err) {
      console.error(err);
      alert("Feedback failed");
    }
  };

  const handleExport = async (fmt) => {
    setExportLoading(true);
    try {
      const res = await api.get(`/api/export/${id}`, {
        params: { format: fmt },
        responseType: "blob",
      });
      const blob = new Blob([res.data], {
        type:
          fmt === "pptx"
            ? "application/vnd.openxmlformats-officedocument.presentationml.presentation"
            : "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
      });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = fmt === "pptx" ? `project_${id}.pptx` : `project_${id}.docx`;
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error(err);
      alert("Export failed");
    } finally {
      setExportLoading(false);
    }
  };

  if (loading) return <p style={{ padding: 20 }}>Loading...</p>;
  if (error) return <p style={{ padding: 20, color: "red" }}>{error}</p>;
  if (!project) return null;

  return (
    <div style={{ padding: 20 }}>
      <h2>
        {project.title} <span style={{ fontSize: 14 }}>({project.doc_type})</span>
      </h2>
      {project.topic && <p><strong>Topic:</strong> {project.topic}</p>}

      <div style={{ margin: "10px 0" }}>
        <button onClick={() => handleExport("docx")} disabled={exportLoading}>
          Export DOCX
        </button>
        <button
          onClick={() => handleExport("pptx")}
          style={{ marginLeft: 8 }}
          disabled={exportLoading}
        >
          Export PPTX
        </button>
      </div>

      {project.sections.map((s) => (
        <div
          key={s.id}
          style={{ border: "1px solid #ddd", padding: 10, marginBottom: 10 }}
        >
          <h3>
            {s.order + 1}. {s.title}
          </h3>
          <pre
            style={{
              whiteSpace: "pre-wrap",
              background: "#fafafa",
              padding: 8,
              borderRadius: 4,
            }}
          >
            {s.content}
          </pre>
          <p>
            Likes: {s.likes} | Dislikes: {s.dislikes}
          </p>

          <div style={{ marginTop: 8 }}>
            <label>Refinement instruction:</label>
            <textarea
              rows={2}
              style={{ width: "100%" }}
              value={refineText[s.id] || ""}
              onChange={(e) =>
                setRefineText((prev) => ({ ...prev, [s.id]: e.target.value }))
              }
              placeholder="e.g. Make this more formal, shorten to 100 words, convert to bullet points..."
            />
            <button onClick={() => refineSection(s.id)} style={{ marginTop: 4 }}>
              Refine with AI
            </button>
          </div>

          <div style={{ marginTop: 8 }}>
            <label>Comment:</label>
            <input
              style={{ width: "100%" }}
              value={commentText[s.id] || ""}
              onChange={(e) =>
                setCommentText((prev) => ({ ...prev, [s.id]: e.target.value }))
              }
              placeholder="Add a note about this section..."
            />
            <div style={{ marginTop: 4 }}>
              <button onClick={() => sendFeedback(s.id, "like")}>Like</button>
              <button
                onClick={() => sendFeedback(s.id, "dislike")}
                style={{ marginLeft: 4 }}
              >
                Dislike
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
