import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { api } from "../api";

export default function ConfigureProject() {
  const [title, setTitle] = useState("");
  const [topic, setTopic] = useState("");
  const [docType, setDocType] = useState("docx");
  const [sections, setSections] = useState([{ title: "", order: 0 }]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const updateSection = (index, field, value) => {
    setSections((prev) =>
      prev.map((s, i) => (i === index ? { ...s, [field]: value } : s))
    );
  };

  const addSection = () => {
    setSections((prev) => [...prev, { title: "", order: prev.length }]);
  };

  const removeSection = (index) => {
    setSections((prev) => prev.filter((_, i) => i !== index).map((s, i) => ({ ...s, order: i })));
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);
    try {
      const outline = sections.map((s, i) => ({
        title: s.title || `Section ${i + 1}`,
        order: i,
      }));
      const res = await api.post("/projects", {
        title,
        topic,
        doc_type: docType,
        outline,
      });
      const projectId = res.data.id;
      // Immediately generate content
      await api.post(`/projects/${projectId}/generate`);
      navigate(`/projects/${projectId}/edit`);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.detail || "Failed to create project");
    } finally {
      setLoading(false);
    }
  };

  return (
  <div className="page">
    <div className="page-header">
      <div>
        <h2 className="page-title">New Project</h2>
        <p className="page-subtitle">
          Describe your topic and let the AI draft a polished document for you.
        </p>
      </div>
    </div>

    <div className="surface">
      {error && <p className="text-danger" style={{ marginBottom: 12 }}>{error}</p>}

      <form className="form" onSubmit={handleSubmit}>
        <div className="form-field">
          <label className="label">Title</label>
          <input
            className="input"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="e.g. Market research on EV adoption"
            required
          />
        </div>

        <div className="form-field">
          <label className="label">Main Topic / Prompt</label>
          <input
            className="input"
            value={topic}
            onChange={(e) => setTopic(e.target.value)}
            placeholder="e.g. Market analysis of EV industry in 2025"
            required
          />
        </div>

        <div className="form-field">
          <label className="label">Document Type</label>
          <select
            className="select"
            value={docType}
            onChange={(e) => setDocType(e.target.value)}
          >
            <option value="docx">Word (.docx)</option>
            <option value="pptx">PowerPoint (.pptx)</option>
          </select>
        </div>

        <div className="form-field">
          <label className="label">Sections</label>
          {sections.map((s, idx) => (
            <div key={idx} className="section-input-row">
              <input
                className="input"
                style={{ flex: 1 }}
                value={s}
                onChange={(e) => updateSectionTitle(idx, e.target.value)}
                placeholder={`Section ${idx + 1} title`}
                required
              />
              {sections.length > 1 && (
                <button
                  type="button"
                  className="btn btn-ghost btn-sm"
                  onClick={() => removeSection(idx)}
                >
                  âœ•
                </button>
              )}
            </div>
          ))}

          <div style={{ marginTop: 12, display: "flex", gap: 10 }}>
            <button
              type="button"
              className="btn btn-ghost"
              onClick={addSection}
            >
              + Add Section
            </button>
            <button type="submit" className="btn btn-primary">
              Create &amp; Generate with AI
            </button>
          </div>
        </div>
      </form>
    </div>
  </div>
);

}
