import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { api } from "../api";

export default function ConfigureProject() {
  const [title, setTitle] = useState("");
  const [topic, setTopic] = useState("");
  const [docType, setDocType] = useState("docx");
  const [sections, setSections] = useState([{ title: "", order: 0 }]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const updateSection = (index, field, value) => {
    setSections((prev) =>
      prev.map((s, i) => (i === index ? { ...s, [field]: value } : s))
    );
  };

  const addSection = () => {
    setSections((prev) => [...prev, { title: "", order: prev.length }]);
  };

  const removeSection = (index) => {
    setSections((prev) => prev.filter((_, i) => i !== index).map((s, i) => ({ ...s, order: i })));
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);
    try {
      const outline = sections.map((s, i) => ({
        title: s.title || `Section ${i + 1}`,
        order: i,
      }));
      const res = await api.post("/api/projects", {
        title,
        topic,
        doc_type: docType,
        outline,
      });
      const projectId = res.data.id;
      // Immediately generate content
      await api.post(`/projects/${projectId}/generate`);
      navigate(`/projects/${projectId}/edit`);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.detail || "Failed to create project");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "20px auto" }}>
      <h2>New Project</h2>
      <form onSubmit={handleCreate}>
        <div>
          <label>Title</label>
          <input
            style={{ width: "100%", marginBottom: 8 }}
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Main Topic / Prompt</label>
          <input
            style={{ width: "100%", marginBottom: 8 }}
            value={topic}
            onChange={(e) => setTopic(e.target.value)}
            placeholder="e.g. Market analysis of EV industry in 2025"
          />
        </div>
        <div>
          <label>Document Type</label>
          <select
            value={docType}
            onChange={(e) => setDocType(e.target.value)}
            style={{ marginBottom: 8 }}
          >
            <option value="docx">Word (.docx)</option>
            <option value="pptx">PowerPoint (.pptx)</option>
          </select>
        </div>

        <h3>{docType === "docx" ? "Sections" : "Slides"}</h3>
        {sections.map((s, i) => (
          <div key={i} style={{ display: "flex", marginBottom: 6 }}>
            <input
              style={{ flex: 1 }}
              placeholder={docType === "docx" ? `Section ${i + 1} title` : `Slide ${i + 1} title`}
              value={s.title}
              onChange={(e) => updateSection(i, "title", e.target.value)}
            />
            <button type="button" onClick={() => removeSection(i)} style={{ marginLeft: 4 }}>
              X
            </button>
          </div>
        ))}
        <button type="button" onClick={addSection} style={{ marginTop: 4 }}>
          + Add {docType === "docx" ? "Section" : "Slide"}
        </button>

        {error && <p style={{ color: "red" }}>{error}</p>}
        <button type="submit" style={{ marginTop: 10 }} disabled={loading}>
          {loading ? "Creating & Generating..." : "Create & Generate with AI"}
        </button>
      </form>
    </div>
  );
}
